CREATE TABLE IF NOT EXISTS `fehler_codes` (
  `id` smallint(6) NOT NULL AUTO_INCREMENT,
  `stoer_code` varchar(4) NOT NULL,
  `zusatz_code` smallint(4) NOT NULL,
  `klasse` varchar(4) NOT NULL,
  `stoerung` tinytext NOT NULL,
  `ursache` mediumtext NOT NULL,
  `massnahme` mediumtext NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `fehler_klassen` (
  `klasse` varchar(4) NOT NULL,
  `text` text NOT NULL,
  `bedeutung` text NOT NULL,
  PRIMARY KEY (`klasse`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

